import React from 'react';
import { useState } from 'react';
import './regSong.css';

export default function RegSong(){
    let trNumber = 0;
    const titleName = "노래 등록하기";
    const htmlTitle = document.querySelector("title");
    htmlTitle.innerHTML= titleName;

    const [songBoard, setSongBoard] = useState({

    });

    const changeValue = (e) =>{
        let target = e;
        if(e.target != undefined){
            taget = e.target;
        }

        setSongBoard({
            ...songBoard,
            [target.name] : target.value
        })
        
    }

    const submitSongFrm = (e) =>{
        e.preventDefault(); //submit의 Action 방지
        
        
    }
    

    const addSongInfo = () =>{
        if(trNumber === 0){    
            trNumber = document.getElementById("trNumber").value;
        }
        trNumber++;        
        const songList_tbody = document.getElementById('songList_tbody');
        const tr = document.createElement('tr');
        let url = makeInputTd("text", "유튜브 주소", "youtubeUrl", "youtubeUrl");
        let answer = makeInputTd("text", "정답", "answer", "answer");
        let hint = makeInputTd("text", "힌트", "hint", "hint");
        let btn = makeButtonTd("button", "remove_btn", trNumber);
        tr.setAttribute("id", "songInfoTr"+trNumber);
        tr.appendChild(url);
        tr.appendChild(answer);
        tr.appendChild(hint);
        tr.appendChild(btn);
       songList_tbody.appendChild(tr);
    }

    const makeInputTd = (input_type, input_placeholder, input_name, input_class) =>{
        let td = document.createElement('td');
        let input = document.createElement('input');
        input.setAttribute('type', input_type);
        input.setAttribute('placeholder', input_placeholder);
        input.setAttribute('name', input_name);
        input.setAttribute('class', input_class);
        input.addEventListener('keydown', ()=>{changeValue(input)});
        td.appendChild(input);
        return td;

    }

    const makeButtonTd = (btn_type, btn_class, btn_num)=>{
        let td = document.createElement('td');
        let btn = document.createElement('button');
        btn.setAttribute('type', btn_type);
        btn.setAttribute('class', btn_class);
        btn.addEventListener("click", ()=>{removeTr(btn_num)});
        btn.innerHTML = "X";
        td.appendChild(btn);
        return td;
    }

    const removeTr = (removeTrNum) =>{
        document.getElementById('songInfoTr'+removeTrNum).remove();
    }

    

    

    return(
        <>
        <section>
            <h1>노래 리스트 만들기</h1>
            <form onSubmit={submitSongFrm}>
            <div className='tbl-header'>
                <table cellSpacing="0" border="0">
                <thead>
                    <tr>
                    <th>제목</th>
                    <th>비밀번호</th>
                    <th>대표 이미지</th>
                    </tr>
                </thead>
                </table>
            </div>
            <div className="tbl-content">
                <table cellSpacing='0' border='0'>
                <tbody>
                    <tr>
                        <td><input type="text" placeholder="제목" name="title" id="title" defaultValue="" onChange={changeValue}/></td>
                        <td><input type="password" placeholder="비밀번호" name="password" id="password" onChange={changeValue}/> </td>
                        <td>이미지 : <input type="file" name="songImg" defaultValue="" onChange={changeValue}/></td>
                    </tr>
                </tbody>
                </table>
            </div>
                <div className="tbl-header">
                <table cellPadding={0} cellSpacing={0} border={0}>
                <thead>
                    <tr>
                    <th>유튜브 주소</th>
                    <th>정답 </th>
                    <th>힌트</th>
                    <th><button type="button" id="add_songList_btn" onClick={addSongInfo}>노래 목록 추가</button></th>
                    </tr>
                </thead>
                </table>
            </div>
            <div className="tbl-content">
                <table cellSpacing="0" border="0">
                <tbody id="songList_tbody">
                    <tr id="songInfoTr1">
                        <td><input type="text" placeholder="유튜브 주소" name="youtubeUrl" className="youtubeUrl" onChange={changeValue}/></td>
                        <td><input type="text" placeholder="정답" name="answer" className="answer" onChange={changeValue}/> </td>
                        <td><input type="text" placeholder="힌트" name="hint" className="hint" onChange={changeValue}/></td>
                        <td><button className="remove_btn" type="button" onClick={()=>{removeTr(1)}} >X</button></td>                        
                    </tr>                        
                </tbody>
                </table>
                <input type="hidden" id="trNumber" value={1}/>
            </div>
                <button className="submit_btn" onClick={submitSongFrm}>등록</button>
            </form>
        </section>
        </>
    );
}
